      },
    };
    try {
      const res = await axios.post("/api/user", formData, config);
      dispatch({
        type: REGISTER_SUCCESS,
        payload: res.data,
      });
      console.log(res.data);
    } catch (err) {
      dispatch({
        type: REGISTER_FAIL,
