import React, {
    useReducer
} from 'react';
import {
    v4 as uuid
} from 'uuid';
import ContactContext from './contactContext';
import contactReducer from './ContactReducer';

import {
    ADD_CONTACT,
    DELETE_CONTACT,
    SET_CURRENT,
    CLEAR_CURRENT,
    UPDATE_CONTACT,
    FILTER_CONTACTS,
    CLEAR_FILTER
} from '../types';

const ContactState = props => {
    const initialState = {
        contacts: [{
                id: 2,
                name: 'Harry',
                email: 'harry@gmail.com',
                phone: '222-333-444',
                type: 'professional'
            },
            {
                id: 3,
                name: 'Harry',
                email: 'harry@gmail.com',
                phone: '222-333-444',
                type: 'personal'
            }
        ],
    };
    const [state, dispatch] = useReducer(contactReducer, initialState);

    //ADD_CONTACT
    const addContact = contact => {
        contact.id = uuid();
        dispatch({
            type: ADD_CONTACT,
            payload: contact
        });
    }

    //DELETE_CONTACT

    const deleteContact = (id) => {
        dispatch({
            type: DELETE_CONTACT,
            payload: id
        });
    }

    //SET_CURRENT

    //CLEAR_CURRENT

    //UPDATE_CONTACT
    const editContact = (id) => {
        dispatch({
            type: UPDATE_CONTACT,
            payload: id
        });
    }

    //FILTER_CONTACTS

    //CLEAR_FILTER

    return ( <
        ContactContext.Provider value = {
            {
                contacts: state.contacts,
                addContact,
                deleteContact,
                editContact
            }
        } > {
            props.children
        } <
        /ContactContext.Provider>
    )
};

export default ContactState;
