import React, {useState, createContext} from 'react';
import ContactContext from '../../context/contacts/contactContext';

const ContactForm = () => {
    const contactContext = createContext(ContactContext);
    const [contact, setContact] = useState({
        name:'',
        email:'',
        phone:'',
        type:'personal'
    });

    const {name, email, phone, type} = contact;

    const onChange = e => setContact({...contact,[e.target.name]:e.target.value});

    return (
        <form>
            <input type="text" name="name" value={name} placeholder="Name" onChange={onChange}/>
            <input type="email" name="email" value={email} placeholder="Email" onChange={onChange}/>
            <input type="text" name="phone" value={phone} placeholder="Phone" onChange={onChange}/>
            <div>
                <input type="radio" name="personal" value="personal" checked={type === 'personal'} onChange={onChange}/> personal {' '}
                <input type="radio" name="professional" value={professional} checked={type === 'professional'} onChange={onChange}/>
                professional
            </div>
        </form>
    )
}

export default ContactForm
